CREATE TABLE IF NOT EXISTS STATUS(ID INTEGER PRIMARY KEY AUTOINCREMENT, DESCRICAO VARCHAR NOT NULL);
CREATE TABLE IF NOT EXISTS CONTRATO(ID INTEGER PRIMARY KEY AUTOINCREMENT, DESCRICAO VARCHAR NOT NULL);
CREATE TABLE IF NOT EXISTS ATIVO(ID INTEGER PRIMARY KEY AUTOINCREMENT, DESCRICAO VARCHAR NOT NULL, VALOR DECIMAL NOT NULL, DATA_COMPRA DATE NOT NULL, DEPRECIACAO DECIMAL, ID_STATUS INTEGER NOT NULL, ID_CONTRATO INTEGER NOT NULL, FOREIGN KEY (ID_STATUS) REFERENCES STATUS(ID), FOREIGN KEY (ID_CONTRATO) REFERENCES CONTRATO(ID));
CREATE TABLE IF NOT EXISTS USERS(USERNAME VARCHAR PRIMARY KEY, ROLE VARCHAR NOT NULL, HASH VARCHAR NOT NULL, EMAIL_ADDR VARCHAR, DESC VARCHAR, CREATION_DATE VARCHAR, LAST_LOGIN VARCHAR, TELEFONE VARCHAR, NOME VARCHAR, FOREIGN KEY (ROLE) REFERENCES ROLES(ROLE));
CREATE TABLE IF NOT EXISTS ROLES(ROLE VARCHAR NOT NULL, LEVEL INTEGER NOT NULL);